import {
  Box,
  Text,
  Badge,
  SimpleGrid,
  Heading,
  Divider,
} from "@chakra-ui/layout";
import { Image } from "@chakra-ui/image";
import { StarIcon } from "@chakra-ui/icons";
import { background, position } from "@chakra-ui/styled-system";
import React from "react";
import { Collapse } from "@chakra-ui/transition";
import { Button } from "@chakra-ui/react";
import { useState } from "react";

const MoviesList = (props) => {
  const movie = props.movie;
  const [show, setShow] = useState(false);
  function handleToggle() {
    setMovie(movie);
  }
  return (
    <Box
      maxW="sm"
      borderWidth="1px"
      borderRadius="lg"
      overflow="hidden"
      key={movie.id}
    >
      <div
        className="background"
        style={{
          backgroundImage: `url(${movie.Poster})`,
          backgroundSize: "cover",
          height: "300px",
        }}
      ></div>

      <Box p="6">
        <Box d="flex" alignItems="baseline">
          <Badge borderRadius="full" px="2" colorScheme="teal">
            {movie.Rated}
          </Badge>
          <Box
            color="gray.500"
            fontWeight="semibold"
            letterSpacing="wide"
            fontSize="xs"
            textTransform="uppercase"
            ml="2"
          >
            {movie.Genre} | {movie.Year}
          </Box>
        </Box>
        <Box
          mt="1"
          fontWeight="semibold"
          as="h4"
          lineHeight="tight"
          isTruncated
        >
          {movie.Title}
        </Box>
        <Box>
          <Box as="span" color="gray.600" fontSize="sm">
            Cast: {movie.Actors}
          </Box>
          <Collapse startingHeight={1} in={movie.show}>
            {movie.Plot}
          </Collapse>
          <Button size="sm" colorScheme="teal" variant="link">
            read {movie.show ? "less" : "more"}
          </Button>
        </Box>
      </Box>
      <Box position="relative" d="flex" mb="0%">
        <StarIcon key={1} color="teal.500" />

        <Box as="span" ml="2" color="gray.600" fontSize="sm">
          {movie.imdbRating}/10
        </Box>
      </Box>
    </Box>
  );
};

export default MoviesList;
