import { useForm } from "react-hook-form";
import { useColorModeValue } from "@chakra-ui/color-mode";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  Input,
  Button,
  Textarea,
  Stack,
  Box,
  Center,
  Flex,
  Heading,
} from "@chakra-ui/react";

const Form = () => {
  const { handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);
  const color = useColorModeValue("gray.100", "gray.400");
  return (
    <Flex height="2xl" alignItems="center" justifyContent="center">
      <form onSubmit={handleSubmit(onSubmit)}>
        <Flex direction="column" bgColor={color} p={12} rounded={6}>
          <Heading size="lg">Add movie</Heading>
          <FormControl id="title" isRequired>
            <FormLabel>Title</FormLabel>
            <Input placeholder="Movie title" />
          </FormControl>
          <FormControl id="cast">
            <FormLabel>Cast</FormLabel>
            <Input placeholder="medium size" size="md" />
          </FormControl>
          <FormControl id="genres">
            <FormLabel>Genres</FormLabel>
            <Input placeholder="medium size" size="md" />
          </FormControl>
          <FormControl id="year">
            <FormLabel>Year</FormLabel>
            <Input placeholder="medium size" size="md" />
          </FormControl>
          <FormControl id="plot">
            <FormLabel>Plot</FormLabel>
            <Textarea placeholder="medium size" size="md" />
          </FormControl>
        </Flex>
      </form>
    </Flex>
  );
};

export default Form;
