import { Heading } from "@chakra-ui/react";

import actorsJSON from "./actorsJSON";
import { SimpleGrid, Divider } from "@chakra-ui/layout";
import ActorsList from "./ActorsList";
import { useState } from "react";
const Actors = () => {
  actorsJSON.forEach((e, index) => {
    e.id = index;
    e.show = false;
  });
  const [actors, setActors] = useState(actorsJSON);

  return (
    <div className="actorsList">
      <Heading ml={10}>Actors & Actresses</Heading>
      <Divider></Divider>
      <SimpleGrid
        ml={10}
        mt={5}
        mr={10}
        spacing={10}
        minChildWidth="200px"
        columnGap="100px"
      >
        {actors.map((actor) => (
          <ActorsList actor={actor} />
        ))}
      </SimpleGrid>
    </div>
  );
};

export default Actors;
