import { Box, Text, Badge, SimpleGrid, Center } from "@chakra-ui/layout";
import { Image } from "@chakra-ui/image";
import { StarIcon } from "@chakra-ui/icons";
import { background, position } from "@chakra-ui/styled-system";

const MoviesList = (props) => {
  const movies = props.movies;
  return (
    <div className="moviesList">
      <SimpleGrid spacing={10} minChildWidth="400px" columnGap="30px">
        {movies.map((movie) => (
          <Box maxW="sm" borderWidth="1px" borderRadius="lg" overflow="hidden">
            <div
              style={{
                backgroundImage: `url(${movie.Poster})`,
                backgroundSize: "cover",
                position: "relative",
                height: "300px",
                zIndex: "-999",
              }}
            ></div>
            <div
              style={{
                backgroundImage: `url(${movie.Poster})`,
                backgroundSize: "cover",
                height: "300px",
                zIndex: "999",
                position: "absolute",
              }}
            ></div>

            <Box p="6">
              <Box d="flex" alignItems="baseline">
                <Badge borderRadius="full" px="2" colorScheme="teal">
                  {movie.Rated}
                </Badge>
                <Box
                  color="gray.500"
                  fontWeight="semibold"
                  letterSpacing="wide"
                  fontSize="xs"
                  textTransform="uppercase"
                  ml="2"
                >
                  {movie.Genre} | {movie.Year}.
                </Box>
              </Box>
              <Box
                mt="1"
                fontWeight="semibold"
                as="h4"
                lineHeight="tight"
                isTruncated
              >
                {movie.Title}
              </Box>
              <Box>
                <Box as="span" color="gray.600" fontSize="sm">
                  Cast: {movie.Actors}
                </Box>
                <Text>{movie.Plot}</Text>
              </Box>
              <Box d="flex" mt="2" alignItems="center">
                <StarIcon key={1} color="teal.500" />

                <Box as="span" ml="2" color="gray.600" fontSize="sm">
                  {movie.imdbRating}/10
                </Box>
              </Box>
            </Box>
          </Box>
        ))}
      </SimpleGrid>
    </div>
  );
};

export default MoviesList;
