import { Heading } from "@chakra-ui/react";

import moviesJSON from "./moviesJSON";
import { SimpleGrid, Divider } from "@chakra-ui/layout";
import MoviesList from "./MoviesList";
import { useState } from "react";
const Movies = () => {
  moviesJSON.forEach((e, index) => {
    e.id = index;
    e.show = false;
  });
  const [movies, setMovies] = useState(moviesJSON);

  return (
    <div className="moviesList">
      <Heading ml={10}>Movies</Heading>
      <Divider></Divider>
      <SimpleGrid
        ml={10}
        mt={5}
        mr={10}
        spacing={10}
        minChildWidth="300px"
        columnGap="100px"
      >
        {movies.map((movie) => (
          <MoviesList movie={movie} />
        ))}
      </SimpleGrid>
    </div>
  );
};

export default Movies;
