import { Flex, Heading, VStack } from "@chakra-ui/layout";
import { useColorMode } from "@chakra-ui/color-mode";
import { FaSun, FaMoon, FaBars } from "react-icons/fa";
import {
  Button,
  HStack,
  IconButton,
  MenuDivider,
  Spacer,
} from "@chakra-ui/react";
import { useMediaQuery } from "@chakra-ui/react";
import React from "react";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuGroup,
  MenuItem,
} from "@chakra-ui/menu";

const Navbar = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const [isSmallScreen] = useMediaQuery("(max-width: 800px)");
  const navContent = isSmallScreen ? (
    <React.Fragment>
      <Spacer />
      <Menu>
        <MenuButton
          as={Button}
          bgColor="teal"
          color="white"
          _hover={{ bg: "teal.700" }}
          _active={{ bg: "teal.700" }}
        >
          <FaBars />
        </MenuButton>
        <MenuList>
          <MenuGroup title="Movies">
            <MenuItem>All Movies</MenuItem>
            <MenuItem>Add Movie</MenuItem>
          </MenuGroup>
          <MenuDivider />
          <MenuGroup title="Actors & Actresses">
            <MenuItem>All Actors & Actresses</MenuItem>
            <MenuItem>Add Actor or Actress</MenuItem>
          </MenuGroup>
          <MenuDivider />
          <MenuItem>Log in</MenuItem>
          <MenuItem onClick={toggleColorMode}>
            {colorMode === "light" ? "Dark Mode" : "Light Mode"}
          </MenuItem>
        </MenuList>
      </Menu>
    </React.Fragment>
  ) : (
    <React.Fragment>
      <HStack spacing="20px" ml="10">
        <Menu>
          <MenuButton>Homepage</MenuButton>
        </Menu>
        <Menu>
          <MenuButton>Movies</MenuButton>
          <MenuList>
            <MenuGroup>
              <MenuItem>All Movies</MenuItem>
              <MenuItem>Add Movie</MenuItem>
            </MenuGroup>
          </MenuList>
        </Menu>
        <Menu>
          <MenuButton>Actors & Actresses</MenuButton>
          <MenuList>
            <MenuGroup>
              <MenuItem>All Actors & Actresses</MenuItem>
              <MenuItem>Add Actor or Actress</MenuItem>
            </MenuGroup>
          </MenuList>
        </Menu>
      </HStack>
      <Spacer />
      <Button bgColor="teal.500" color="white" _hover={{ bg: "teal.700" }}>
        Log in
      </Button>
      <IconButton
        ml={8}
        icon={colorMode === "light" ? <FaMoon /> : <FaSun />}
        isRound="true"
        onClick={toggleColorMode}
      />
    </React.Fragment>
  );
  return (
    <VStack p={5}>
      <Flex w="100%">
        <Heading ml="8" size="md">
          FSND Casting Agency
        </Heading>
        {navContent}
      </Flex>
    </VStack>
  );
};

export default Navbar;
