import {
  Box,
  Text,
  Badge,
  SimpleGrid,
  Heading,
  Divider,
} from "@chakra-ui/layout";
import { Image } from "@chakra-ui/image";
import { StarIcon } from "@chakra-ui/icons";
import { background, position } from "@chakra-ui/styled-system";
import moviesJSON from "./moviesJSON";
import React from "react";
import { Collapse } from "@chakra-ui/transition";
import { Button } from "@chakra-ui/react";
import { useState } from "react";

const MoviesList = () => {
  moviesJSON.forEach((e, index) => {
    e.id = index;
    e.show = false;
  });
  const [movies, setMovies] = useState(moviesJSON);

  function handleToggle(movie) {
    this.set(!movie.show);
  }
  return (
    <div className="moviesList">
      <Heading ml={10}>Movies</Heading>
      <Divider></Divider>
      <SimpleGrid
        ml={10}
        mt={5}
        spacing={10}
        minChildWidth="400px"
        columnGap="30px"
      >
        {movies.map((movie) => (
          <Box
            maxW="sm"
            borderWidth="1px"
            borderRadius="lg"
            overflow="hidden"
            key={movie.id}
          >
            <div
              className="background"
              style={{
                backgroundImage: `url(${movie.Poster})`,
                backgroundSize: "cover",
                height: "300px",
              }}
            ></div>

            <Box p="6">
              <Box d="flex" alignItems="baseline">
                <Badge borderRadius="full" px="2" colorScheme="teal">
                  {movie.Rated}
                </Badge>
                <Box
                  color="gray.500"
                  fontWeight="semibold"
                  letterSpacing="wide"
                  fontSize="xs"
                  textTransform="uppercase"
                  ml="2"
                >
                  {movie.Genre} | {movie.Year}
                </Box>
              </Box>
              <Box
                mt="1"
                fontWeight="semibold"
                as="h4"
                lineHeight="tight"
                isTruncated
              >
                {movie.Title}
              </Box>
              <Box>
                <Box as="span" color="gray.600" fontSize="sm">
                  Cast: {movie.Actors}
                </Box>
                <Collapse startingHeight={1} in={movie.show}>
                  {movie.Plot}
                </Collapse>
                <Button
                  size="sm"
                  onClick={handleToggle}
                  colorScheme="teal"
                  variant="link"
                >
                  read {movie.show ? "less" : "more"}
                </Button>
              </Box>
            </Box>
            <Box position="relative" d="flex" mb="0%">
              <StarIcon key={1} color="teal.500" />

              <Box as="span" ml="2" color="gray.600" fontSize="sm">
                {movie.imdbRating}/10
              </Box>
            </Box>
          </Box>
        ))}
      </SimpleGrid>
    </div>
  );
};

export default MoviesList;
